import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import HotelList from './HotelList'; // Asegúrate de que la ruta sea correcta
import './GestiondeAlojamientos.css';

const GestiondeAlojamientos = ({ role }) => {
  const navigate = useNavigate();
  const [hoteles, setHoteles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHoteles(); // Llamamos a la función para obtener hoteles al cargar el componente
  }, []);

  const fetchHoteles = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/getAlojamientos');
      const data = await response.json();
      // Formateamos los hoteles según los datos devueltos por la API
      const formattedHotels = data.map(hotel => ({
        id: hotel.idAlojamiento,
        name: hotel.Titulo,
        latitud: hotel.Latitud,
        longitud: hotel.Longitud,
        price: hotel.PrecioPorDia,
        images: hotel.imagenes || [], // Ajusta si hay datos de imágenes
        description: hotel.Descripción, // Añade otras propiedades si son necesarias
        bedrooms: hotel.CantidadDormitorios,
        bathrooms: hotel.CantidadBanios,
        estado: hotel.Estado
      }));
      setHoteles(formattedHotels);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching hotels:', error);
      setLoading(false);
    }
  };

  const handleDeleteHotel = async (id) => {
    try {
      await fetch(`http://localhost:3001/alojamiento/deleteAlojamiento/${id}`, {
        method: 'DELETE',
      });
      setHoteles(hoteles.filter(hotel => hotel.id !== id));
    } catch (error) {
      console.error('Error deleting hotel:', error);
    }
  };

  const handleUpdateHotel = async (id, updatedHotel) => {
    try {
      await fetch(`http://localhost:3001/alojamiento/putAlojamiento/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedHotel),
      });
      setHoteles(hoteles.map(hotel => (hotel.id === id ? updatedHotel : hotel)));
    } catch (error) {
      console.error('Error updating hotel:', error);
    }
  };

  const handleCheckInClick = () => {
    navigate('/');
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <button className="checkin-button" onClick={handleCheckInClick}>
          IDW - CheckIn
        </button>
        <h2>Gestión de Alojamientos</h2>
        <nav>
          <ul>
            {role === 'admin' && <li>Gestión de Usuarios</li>}
            <li>Ver Alojamientos</li>
            {role === 'manager' && <li>Editar Alojamientos</li>}
            <li>Perfil</li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <h1>Bienvenido, {role === 'admin' ? 'Administrador' : 'Usuario'}</h1>
        <div className="hotel-list">
          {loading ? (
            <p>Cargando Hoteles...</p>
          ) : (
            <HotelList hotels={hoteles} onDeleteHotel={handleDeleteHotel} onUpdateHotel={handleUpdateHotel} />
          )}
        </div>
      </main>
    </div>
  );
};

export default GestiondeAlojamientos;
