import React, { Component } from 'react';
import './GestiondeAlojamientos.css'; // Importa el archivo CSS para los estilos

class GestiondeAlojamientos extends Component {
  render() {
    const { role } = this.props; // Recibe el rol del usuario como prop

    return (
      <div className="dashboard">
        <aside className="sidebar">
          <h2>Gestión de Alojamientos</h2>
          <nav>
            <ul>
              {role === 'admin' && <li>Gestión de Usuarios</li>}
              <li>Ver Alojamientos</li>
              {role === 'manager' && <li>Editar Alojamientos</li>}
              <li>Perfil</li>
            </ul>
          </nav>
        </aside>
        <main className="main-content">
          <h1>Bienvenido, {role === 'admin' ? 'Administrador' : 'Usuario'}</h1>
          {/* Aquí se pueden añadir componentes o contenido según el rol */}
        </main>
      </div>
    );
  }
}

export default GestiondeAlojamientos;
