import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import validation from './LoginValidation';

function Login() {
    const [values, setValues] = useState({
        email: '',
        password: ''
    });

    const [error, setError] = useState({});

    const handleInput = (event) => {
        setValues(prev => ({...prev, [event.target.name]: event.target.value}));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const errors = validation(values);
        setError(errors);
        if (Object.keys(errors).length === 0) {
            // Aquí puedes enviar los datos del formulario si no hay errores
            console.log("Formulario válido. Datos enviados:", values);
        }
    }

    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <h2 className='text-center'>Crear cuenta</h2>
                <form action="" onSubmit={handleSubmit}>

                    <div className='mb-3'>
                        <label htmlFor="email">Correo</label>
                        <input type="email" id="email" placeholder='Ingrese su correo' name='email'
                            onChange={handleInput}
                            value={values.email}
                            className='form-control rounded-0'/>
                        {error.email && <span className='text-danger'> {error.email} </span>}
                    </div>

                    <div className='mb-3'>
                        <label htmlFor="password">Contraseña</label>
                        <input type="password" id="password" placeholder='Ingrese su contraseña' name='password'
                            onChange={handleInput}
                            value={values.password}
                            className='form-control rounded-0'/>
                        {error.password && <span className='text-danger'> {error.password} </span>}
                    </div>

                    <button type='submit' className='btn btn-success w-100 rounded-0'>Ingresar</button>

                    <p>¿No tiene cuenta? Click abajo</p>
                    <Link to="/signup" className='btn btn-default border w-100 text-decoration-non' type="button">Crear Cuenta</Link>

                </form>
            </div>
        </div>
    );
}

export default Login;
