import React, { useState } from 'react';
import './Login.css'; // Estilo CSS para el formulario de login

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevenir el envío del formulario por defecto
    // Aquí puedes realizar la lógica de autenticación, por ejemplo, una llamada a tu API
    // Pero para este ejemplo, simplemente llamaremos a la función onLogin con los datos ingresados
    onLogin({ username, password });
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Iniciar Sesion</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Usuario</label>
            <input
              type="text"
              value={username}
              onChange={handleUsernameChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Contraseña</label>
            <input
              type="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
          <button type="submit">Ingresar</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
