import React, { useState, useEffect } from 'react';
import './HotelCard.css';
import obtenerCiudadCoordenadas from './obtenerCiudadCoordenadas'; // Ajusta la ruta según donde esté definida la función

const HotelCard = ({ hotel, onDeleteHotel, onUpdateHotel }) => {
  const [city, setCity] = useState('');

  useEffect(() => {
    const fetchCity = async () => {
      const cityName = await obtenerCiudadCoordenadas(hotel.Latitud, hotel.Longitud);
      setCity(cityName);
    };

    fetchCity();
  }, [hotel.Latitud, hotel.Longitud]);

  const handleUpdate = () => {
    const updatedHotel = {
      ...hotel,
      Titulo: 'Título actualizado',
      PrecioPorDia: hotel.PrecioPorDia + 10,
      Rating: hotel.Rating + 0.1
    };
    onUpdateHotel(hotel.id, updatedHotel);
  };

  return (
    <div className="hotel-card">
      <h2>{hotel.Titulo}</h2>
      <p>City: {city}</p>
      <p>Price: ${hotel.PrecioPorDia}</p>
      <div className="hotel-images">
        {hotel.Imagenes && hotel.Imagenes.length > 0 ? (
          hotel.Imagenes.map((image) => (
            <img key={image.id} src={image.url} alt={`Hotel ${hotel.Titulo}`} />
          ))
        ) : (
          <p>No images available</p>
        )}
      </div>
      <button onClick={() => onDeleteHotel(hotel.id)}>Eliminar</button>
      <button onClick={handleUpdate}>Actualizar</button>
    </div>
  );
};

export default HotelCard;
