import React, { useState } from 'react';
import './Filter.css';

const Filter = ({ onFilterChange, onClearFilters }) => {
  const [location, setLocation] = useState('');
  const [hotelName, setHotelName] = useState('');
  const [priceOrder, setPriceOrder] = useState('');

  const handleFilterChange = () => {
    onFilterChange({
      location,
      hotelName,
      priceOrder,
    });
  };

  const handleClearFilters = () => {
    setLocation('');
    setHotelName('');
    setPriceOrder('');
    onClearFilters();
  };

  return (
    <div className="filter-container">
      <div className="filter">
        <h2>Filtrar Hoteles</h2>
        <div>
          <label>Buscar Ciudad o Hotel:</label>
          <input
            type="text"
            placeholder="Buscar por ciudad o nombre de hotel"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </div>
        <div>
          <label>Ordenar por Precio:</label>
          <select value={priceOrder} onChange={(e) => setPriceOrder(e.target.value)}>
            <option value="">Seleccionar</option>
            <option value="asc">Menor a Mayor</option>
            <option value="desc">Mayor a Menor</option>
          </select>
        </div>
        <button onClick={handleFilterChange} className="apply-filters-button">
          Aplicar Filtros
        </button>
        <button onClick={handleClearFilters} className="clear-filters-button">
          Limpiar Filtros
        </button>
      </div>
    </div>
  );
};

export default Filter;
