import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelListUser from '../components/HotelListUser';
import Filter from '../components/Filter'; // Asegúrate de importar el componente Filter

function Servicios() {
  const [hotels, setHotels] = useState([]);
  const [filteredHotels, setFilteredHotels] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchHotelImage = async (idAlojamiento) => {
    try {
      const response = await fetch(`http://localhost:3001/imagen/getImagen/${idAlojamiento}`);
      const data = await response.json();
      return data.RutaArchivo;
    } catch (error) {
      console.error('Error fetching hotel image:', error);
      return null;
    }
  };

  const fetchHotels = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/getAlojamientos');
      const data = await response.json();
      const formattedHotels = await Promise.all(
        data.map(async (hotel) => {
          const image = await fetchHotelImage(hotel.idAlojamiento);
          return {
            id: hotel.idAlojamiento,
            name: hotel.Titulo,
            latitud: hotel.Latitud,
            longitud: hotel.Longitud,
            price: hotel.PrecioPorDia,
            images: hotel.imagenes || [],
            description: hotel.Descripcion,
            bedrooms: hotel.CantidadDormitorios,
            bathrooms: hotel.CantidadBanios,
            estado: hotel.Estado,
            tipoAlojamiento: hotel.TipoAlojamiento,
            image: image || 'default_image_url.jpg', // URL de imagen por defecto
          };
        })
      );
      setHotels(formattedHotels);
      setFilteredHotels(formattedHotels);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching hotels:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHotels();
  }, []);

  const handleFilterChange = (filters) => {
    console.log('Filters:', filters);

    let filtered = hotels.filter((hotel) => {
      const tipoAlojamientoMatch = !filters.type || hotel.tipoAlojamiento === parseInt(filters.type, 10);
      const disponibilidadMatch = !filters.estado || hotel.estado === filters.estado;
      const priceMatch = (!filters.minPrice || hotel.price >= filters.minPrice) &&
                         (!filters.maxPrice || hotel.price <= filters.maxPrice);
      const bedroomsMatch = !filters.bedrooms || hotel.bedrooms >= filters.bedrooms;
      const bathroomsMatch = !filters.bathrooms || hotel.bathrooms >= filters.bathrooms;

      return tipoAlojamientoMatch && disponibilidadMatch && priceMatch && bedroomsMatch && bathroomsMatch;
    });

    console.log('Filtered Hotels:', filtered);
    setFilteredHotels(filtered);
  };

  const handleClearFilters = () => {
    setFilteredHotels(hotels);
  };

  return (
    <>
      <Navbar />
      <Hero
        cName="hero-mid"
        heroImg="https://i.postimg.cc/qqBp3KdY/heather-gill-Wlhfy-AHmp-II-unsplash.jpg"
        title="Alojamientos"
        btnClass="hide"
      />
      <div className="catalog">
        <Filter onFilterChange={handleFilterChange} onClearFilters={handleClearFilters} />
        {loading ? (
          <p>Cargando Hoteles...</p>
        ) : (
          <HotelListUser hotels={filteredHotels} />
        )}
      </div>
      <Footer />
    </>
  );
}

export default Servicios;
