import React, { useState } from 'react';

const RegistrarAlojamiento = ({ onCreate }) => {
  const [formValues, setFormValues] = useState({
    name: '',
    latitud: '',
    longitud: '',
    price: '',
    description: '',
    bedrooms: '',
    bathrooms: '',
    estado: 'Disponible',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleCreate = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/createAlojamiento', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          Titulo: formValues.name,
          Descripción: formValues.description,
          TipoAlojamiento: 1, // Ajustar según tu API
          Latitud: parseFloat(formValues.latitud),
          Longitud: parseFloat(formValues.longitud),
          PrecioPorDia: parseFloat(formValues.price),
          CantidadDormitorios: parseInt(formValues.bedrooms),
          CantidadBanios: parseInt(formValues.bathrooms),
          Estado: formValues.estado,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Alojamiento creado correctamente:', data);
        onCreate(data);
        clearForm();
      } else {
        throw new Error('Error al crear el alojamiento');
      }
    } catch (error) {
      console.error('Error al crear el alojamiento:', error);
    }
  };

  const clearForm = () => {
    setFormValues({
      name: '',
      latitud: '',
      longitud: '',
      price: '',
      description: '',
      bedrooms: '',
      bathrooms: '',
      estado: 'Disponible',
    });
  };

  return (
    <div className="registrar-alojamiento">
      <h2>Registrar Alojamiento</h2>
      <form onSubmit={(e) => { e.preventDefault(); handleCreate(); }}>
        {/* Resto del formulario y manejo de eventos */}
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
};

export default RegistrarAlojamiento;
