import React, { useState, useEffect } from 'react';
import './HotelCard.css';
import obtenerCiudadCoordenadas from '../utils/obtenerCiudadCoordenadas'; // Ajusta la ruta según tu estructura

const HotelCard = ({ hotel, onDeleteHotel, onUpdateHotel }) => {
  const [ciudad, setCiudad] = useState('');

  useEffect(() => {
    obtenerCiudadCoordenadas(hotel.latitud, hotel.longitud)
      .then(data => setCiudad(data.city))
      .catch(error => console.error('Error fetching city:', error));
  }, [hotel.latitud, hotel.longitud]);

  const handleUpdate = () => {
    const updatedHotel = {
      ...hotel,
      Titulo: 'Título actualizado',
      PrecioPorDia: hotel.PrecioPorDia + 10,
      Rating: hotel.Rating + 0.1
    };
    onUpdateHotel(hotel.id, updatedHotel);
  };

  return (
    <div className="hotel-card">
      <h2>{hotel.name}</h2>
      <p>Ciudad: {ciudad}</p>
      <p>Price: ${hotel.price}</p>
      <div className="hotel-images">
        {hotel.images && hotel.images.length > 0 ? (
          hotel.images.map((image) => (
            <img key={image.id} src={image.url} alt={`Hotel ${hotel.name}`} />
          ))
        ) : (
          <p>No images available</p>
        )}
      </div>
      <button onClick={() => onDeleteHotel(hotel.id)}>Eliminar</button>
      <button onClick={handleUpdate}>Actualizar</button>
    </div>
  );
};

export default HotelCard;
