class AdministradordeImagenes {
    static async crearImagen(idAlojamiento, rutaArchivo) {
      try {
        const response = await fetch('http://localhost:3001/imagen/createImagen', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            idAlojamiento,
            RutaArchivo: rutaArchivo,
          }),
        });
  
        if (!response.ok) {
          throw new Error('Error al crear la imagen');
        }
  
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Error al crear la imagen:', error.message);
        throw error;
      }
    }
  
    static async obtenerImagenPorId(idImagen) {
      try {
        const response = await fetch(`http://localhost:3001/imagen/getImagen/${idImagen}`);
  
        if (!response.ok) {
          throw new Error('Error al obtener la imagen');
        }
  
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Error al obtener la imagen:', error.message);
        throw error;
      }
    }
  
    static async actualizarImagen(idImagen, idAlojamiento, rutaArchivo) {
      try {
        const response = await fetch(`http://localhost:3001/imagen/updateImagen/${idImagen}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            idAlojamiento,
            RutaArchivo: rutaArchivo,
          }),
        });
  
        if (!response.ok) {
          throw new Error('Error al actualizar la imagen');
        }
  
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Error al actualizar la imagen:', error.message);
        throw error;
      }
    }
  
    static async eliminarImagen(idImagen) {
      try {
        const response = await fetch(`http://localhost:3001/imagen/deleteImagen/${idImagen}`, {
          method: 'DELETE',
        });
  
        if (!response.ok) {
          throw new Error('Error al eliminar la imagen');
        }
  
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Error al eliminar la imagen:', error.message);
        throw error;
      }
    }
  
    static async obtenerTodasLasImagenes() {
      try {
        const response = await fetch('http://localhost:3001/imagen/getAllImagenes');
  
        if (!response.ok) {
          throw new Error('Error al obtener todas las imágenes');
        }
  
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Error al obtener todas las imágenes:', error.message);
        throw error;
      }
    }
  
    static async guardarImagenEnCarpeta(rutaArchivo, imagenBlob) {
      const carpetaImagenes = 'imagenes'; // Ruta relativa donde guardarás las imágenes
  
      // Si la carpeta no existe, se crea (esto es solo un ejemplo, en navegador no se usa fs)
      if (!window.fs || !window.fs.existsSync(carpetaImagenes)) {
        console.warn('No se puede crear una carpeta en el navegador.');
        return null;
      }
  
      const rutaCompleta = `${carpetaImagenes}/${rutaArchivo}`;
  
      try {
        // Convierte el blob en una URL de objeto
        const blobUrl = window.URL.createObjectURL(imagenBlob);
  
        // Crea un elemento a para descargar el archivo
        const a = document.createElement('a');
        a.style.display = 'none';
        a.href = blobUrl;
        a.download = rutaArchivo;
        document.body.appendChild(a);
        a.click();
  
        // Libera el objeto URL
        window.URL.revokeObjectURL(blobUrl);
  
        console.log(`Imagen guardada en ${rutaCompleta}`);
        return rutaCompleta;
      } catch (error) {
        console.error('Error al guardar la imagen:', error.message);
        throw error;
      }
    }
  }
  
  export default AdministradordeImagenes;
