import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Importa axios para hacer peticiones HTTP
import './RegistrarAlojamiento.css'; // Estilos del formulario
import Sidebar from './Sidebar'; // Componente Sidebar
import Modal from './Modal'; // Componente Modal

const RegistrarAlojamiento = () => {
  const navigate = useNavigate(); // Función navigate de React Router
  const [formValues, setFormValues] = useState({
    name: '',
    latitud: '',
    longitud: '',
    price: '',
    description: '',
    bedrooms: '',
    bathrooms: '',
    estado: 'Disponible',
  });

  const [isModalOpen, setIsModalOpen] = useState(false); // Estado del modal
  const [modalMessage, setModalMessage] = useState(''); // Mensaje del modal

  // Manejar cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  // Manejar subida de imagen a ImgBB
  const handleImageUpload = async (imageFile) => {
    try {
      const formData = new FormData();
      formData.append('image', imageFile);

      // Hacer la petición POST a ImgBB para subir la imagen
      const response = await axios.post('https://api.imgbb.com/1/upload?key=your_api_key', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        return response.data.data.url; // Retornar la URL de la imagen
      } else {
        throw new Error('Error al subir la imagen a ImgBB');
      }
    } catch (error) {
      console.error('Error al subir la imagen:', error);
      throw error;
    }
  };

  // Manejar la creación del alojamiento y la imagen
  const handleCreate = async (e) => {
    e.preventDefault(); // Evitar el envío por defecto del formulario

    try {
      const imageFile = e.target.image.files[0];
      const imageUrl = await handleImageUpload(imageFile); // Subir la imagen y obtener la URL

      // Enviar los datos del formulario al backend de alojamientos
      const responseAlojamiento = await fetch('http://localhost:3001/alojamiento/createAlojamiento', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          Titulo: formValues.name,
          Descripcion: formValues.description,
          TipoAlojamiento: 1, // Ajustar según tu lógica
          Latitud: parseFloat(formValues.latitud),
          Longitud: parseFloat(formValues.longitud),
          PrecioPorDia: parseFloat(formValues.price),
          CantidadDormitorios: parseInt(formValues.bedrooms),
          CantidadBanios: parseInt(formValues.bathrooms),
          Estado: formValues.estado,
        }),
      });

      const dataAlojamiento = await responseAlojamiento.json();

      if (!responseAlojamiento.ok) {
        throw new Error('Error al crear el alojamiento:', dataAlojamiento.message || responseAlojamiento.statusText);
      }

      // Enviar la URL de la imagen al backend de imágenes
      const responseImagen = await fetch('http://localhost:3001/imagen/createImagen', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idAlojamiento: dataAlojamiento.id, // Usar el ID de alojamiento creado
          RutaArchivo: imageUrl, // Enviar la URL de la imagen al backend de imágenes
        }),
      });

      const dataImagen = await responseImagen.json();

      if (!responseImagen.ok) {
        throw new Error('Error al crear la imagen:', dataImagen.message || responseImagen.statusText);
      }

      console.log('Alojamiento y imagen creados correctamente');
      clearForm(); // Limpiar el formulario después de éxito
      setModalMessage('Alojamiento y imagen creados correctamente');
      setIsModalOpen(true); // Mostrar el modal después de manejar la respuesta
    } catch (error) {
      console.error('Error al crear el alojamiento y la imagen:', error);
      setModalMessage('Error al crear el alojamiento y la imagen');
      setIsModalOpen(true);
    }
  };

  // Limpiar el formulario
  const clearForm = () => {
    setFormValues({
      name: '',
      latitud: '',
      longitud: '',
      price: '',
      description: '',
      bedrooms: '',
      bathrooms: '',
      estado: 'Disponible',
    });
  };

  // Funciones de navegación
  const handleCheckInClick = () => {
    navigate('/'); // Navegar a la página de inicio
  };

  const handleRegistrarAlojamientoClick = () => {
    navigate('/registrar-alojamiento'); // Navegar a la página de registro de alojamiento (actual)
  };

  const handleAdministrarImagenesClick = () => {
    navigate('/administrador-imagenes'); // Navegar a la página de administración de imágenes
  };

  const handleGestionAlojamientosClick = () => {
    navigate('/gestion-de-alojamientos'); // Navegar a la página de gestión de alojamientos
  };

  return (
    <div className="dashboard">
      <Sidebar
        role="admin"
        handleRegistrarAlojamientoClick={handleRegistrarAlojamientoClick}
        handleAdministrarImagenesClick={handleAdministrarImagenesClick}
        handleCheckInClick={handleCheckInClick}
        handleGestionAlojamientosClick={handleGestionAlojamientosClick}
      />
      <main className="main-content">
        <div className="registrar-alojamiento">
          <h2>Registrar Alojamiento</h2>
          <form onSubmit={handleCreate} className="form-container">
            {/* Campos del formulario */}
            <div className="form-group">
              <label>Nombre:</label>
              <input type="text" name="name" value={formValues.name} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Latitud:</label>
              <input type="text" name="latitud" value={formValues.latitud} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Longitud:</label>
              <input type="text" name="longitud" value={formValues.longitud} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Precio:</label>
              <input type="text" name="price" value={formValues.price} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Descripción:</label>
              <textarea name="description" value={formValues.description} onChange={handleChange} className="form-input textarea" />
            </div>
            <div className="form-group">
              <label>Dormitorios:</label>
              <input type="text" name="bedrooms" value={formValues.bedrooms} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Baños:</label>
              <input type="text" name="bathrooms" value={formValues.bathrooms} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Estado:</label>
              <input type="text" name="estado" value={formValues.estado} onChange={handleChange} className="form-input" />
            </div>
            {/* Subida de imagen */}
            <div className="form-group">
              <label>Imagen:</label>
              <input type="file" name="image" className="form-input" />
            </div>
            {/* Botón para registrar */}
            <div className="button-group">
              <button type="submit" className="form-button">Registrar</button>
            </div>
          </form>
        </div>
      </main>
      {isModalOpen && <Modal message={modalMessage} onClose={() => setIsModalOpen(false)} />}
    </div>
  );
};

export default RegistrarAlojamiento;
