import React from 'react';
import './HotelCard.css';

const HotelCard = ({ hotel, onDeleteHotel, onUpdateHotel }) => {
  const handleUpdate = () => {
    const updatedHotel = {
      ...hotel,
      Titulo: 'Título actualizado',
      PrecioPorDia: hotel.PrecioPorDia + 10,
      Rating: hotel.Rating + 0.1
    };
    onUpdateHotel(hotel.idAlojamiento, updatedHotel);
  };

  return (
    <div className="hotel-card">
      <h2>{hotel.Titulo}</h2>
      <p>{hotel.Ubicacion}</p>
      <p>Rating: {hotel.Rating}</p>
      <p>Price: ${hotel.PrecioPorDia}</p>
      <div className="hotel-images">
        {hotel.images && hotel.images.length > 0 ? (
          hotel.images.map((image) => (
            <img key={image.id} src={image.url} alt={`Hotel ${hotel.Titulo}`} />
          ))
        ) : (
          <p>No images available</p>
        )}
      </div>
      <button onClick={() => onDeleteHotel(hotel.idAlojamiento)}>Eliminar</button>
      <button onClick={handleUpdate}>Actualizar</button>
    </div>
  );
};

export default HotelCard;
