import React, { useState, useEffect } from 'react';
import './HotelCard.css';
import obtenerCiudadCoordenadas from '../components/obtenerCiudadCoordenadas'; // Asegúrate de que la ruta sea correcta

const HotelCard = ({ hotel, onDeleteHotel, onUpdateHotel }) => {
  const [ciudad, setCiudad] = useState('');
  const [loadingCiudad, setLoadingCiudad] = useState(true);

  useEffect(() => {
    async function fetchCity() {
      try {
        const city = await obtenerCiudadCoordenadas(hotel.Latitud, hotel.Longitud);
        setCiudad(city);
        setLoadingCiudad(false);
      } catch (error) {
        console.error('Error fetching city:', error);
        setLoadingCiudad(false);
      }
    }

    fetchCity();
  }, [hotel.Latitud, hotel.Longitud]);

  const handleUpdate = () => {
    const updatedHotel = {
      ...hotel,
      Titulo: 'Título actualizado',
      PrecioPorDia: hotel.PrecioPorDia + 10,
      Rating: hotel.Rating + 0.1
    };
    onUpdateHotel(hotel.id, updatedHotel);
  };

  return (
    <div className="hotel-card">
      <h2>{hotel.Titulo}</h2>
      {loadingCiudad ? (
        <p>Cargando ciudad...</p>
      ) : (
        <p>{ciudad ? `Ciudad: ${ciudad}` : 'Ciudad no disponible'}</p>
      )}
      <p>Precio: ${hotel.PrecioPorDia}</p>
      <div className="hotel-images">
        {hotel.imagenes && hotel.imagenes.length > 0 ? (
          hotel.imagenes.map((image) => (
            <img key={image.id} src={image.url} alt={`Hotel ${hotel.Titulo}`} />
          ))
        ) : (
          <p>No hay imágenes disponibles</p>
        )}
      </div>
      <button onClick={() => onDeleteHotel(hotel.id)}>Eliminar</button>
      <button onClick={handleUpdate}>Actualizar</button>
    </div>
  );
};

export default HotelCard;
