import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelList from '../components/HotelList';
import Filter from '../components/Filter';

function Servicios() {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/hotels')  // Reemplaza 'http://localhost:3001/api/hotels' con la URL de tu API
      .then(response => response.json())
      .then(data => {
        setHotels(data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, []);

  const handleFilterChange = (filters) => {
    const filteredHotels = hotels.filter((hotel) => {
      return (
        (!filters.location || hotel.location.toLowerCase().includes(filters.location.toLowerCase())) &&
        (filters.minRating === 0 || hotel.rating >= filters.minRating) &&
        (filters.maxPrice === 0 || hotel.price <= filters.maxPrice)
      );
    });
    setHotels(filteredHotels);
  };

  const handleClearFilters = () => {
    fetch('http://localhost:5000/api/hotels')
      .then(response => response.json())
      .then(data => setHotels(data))
      .catch(error => setError(error));
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading hotels: {error.message}</p>;

  return (
    <>
      <Navbar />
      <Hero
        cName="hero-mid"
        heroImg="https://i.postimg.cc/qqBp3KdY/heather-gill-Wlhfy-AHmp-II-unsplash.jpg"
        title="Servicios"
        btnClass="hide"
      />
      <div className="catalog">
        <Filter onFilterChange={handleFilterChange} onClearFilters={handleClearFilters} />
        <HotelList hotels={hotels} />
      </div>
      <Footer />
    </>
  );
}

export default Servicios;
