import axios from 'axios';

const obtenerCiudadCoordenadas = async (latitude, longitude) => {
  try {
    const response = await axios.get(`https://nominatim.openstreetmap.org/reverse?lat=${latitude}&lon=${longitude}&format=json`);
    if (response.data && response.data.address) {
      return response.data.address.city || response.data.address.town || response.data.address.village || 'Ciudad no encontrada';
    } else {
      return 'Ciudad no encontrada';
    }
  } catch (error) {
    console.error('Error al obtener la ciudad:', error);
    return 'Error al obtener la ciudad';
  }
};

export default obtenerCiudadCoordenadas;
