import React, { useState } from 'react';
import './Filter.css';

const Filter = ({ onFilterChange, onClearFilters }) => {
  const [location, setLocation] = useState('');
  const [minRating, setMinRating] = useState(0);
  const [maxPrice, setMaxPrice] = useState(0);

  const handleFilterChange = () => {
    onFilterChange({ location, 
      minRating, 
      maxPrice });
  };

  const handleClearFilters = () => {
    setLocation('');
    setMinRating(0);
    setMaxPrice(0);
    onClearFilters();
  };

  return (
    <div className="filter-container">
      <div className="filter">
        <h2>Filtrar Hoteles</h2>
        <div>
          <label>Ubicación:</label>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </div>
        <div>
          <label>Valoración Mínima:</label>
          <input
            type="number"
            value={minRating}
            onChange={(e) => setMinRating(Number(e.target.value))}
          />
        </div>
        <div>
          <label>Precio Máximo:</label>
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(Number(e.target.value))}
          />
        </div>
        <button onClick={handleFilterChange} className="apply-filters-button">Aplicar Filtros</button>
        <button onClick={handleClearFilters} className="clear-filters-button">Limpiar Filtros</button>
      </div>
    </div>
  );
};

export default Filter;
