import fs from 'fs';
import path from 'path';
import fetch from 'node-fetch';

class AdministradordeImagenes {
  static async crearImagen(idAlojamiento, rutaArchivo) {
    try {
      const response = await fetch('http://localhost:3001/imagen/createImagen', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idAlojamiento,
          RutaArchivo: rutaArchivo,
        }),
      });

      if (!response.ok) {
        throw new Error('Error al crear la imagen');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error al crear la imagen:', error.message);
      throw error;
    }
  }

  static async obtenerImagenPorId(idImagen) {
    try {
      const response = await fetch(`http://localhost:3001/imagen/getImagen/${idImagen}`);

      if (!response.ok) {
        throw new Error('Error al obtener la imagen');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error al obtener la imagen:', error.message);
      throw error;
    }
  }

  static async actualizarImagen(idImagen, idAlojamiento, rutaArchivo) {
    try {
      const response = await fetch(`http://localhost:3001/imagen/updateImagen/${idImagen}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idAlojamiento,
          RutaArchivo: rutaArchivo,
        }),
      });

      if (!response.ok) {
        throw new Error('Error al actualizar la imagen');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error al actualizar la imagen:', error.message);
      throw error;
    }
  }

  static async eliminarImagen(idImagen) {
    try {
      const response = await fetch(`http://localhost:3001/imagen/deleteImagen/${idImagen}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Error al eliminar la imagen');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error al eliminar la imagen:', error.message);
      throw error;
    }
  }

  static async obtenerTodasLasImagenes() {
    try {
      const response = await fetch('http://localhost:3001/imagen/getAllImagenes');

      if (!response.ok) {
        throw new Error('Error al obtener todas las im치genes');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error al obtener todas las im치genes:', error.message);
      throw error;
    }
  }

  static async guardarImagenEnCarpeta(rutaArchivo, imagenBuffer) {
    const carpetaImagenes = path.join(__dirname, 'imagenes');

    if (!fs.existsSync(carpetaImagenes)) {
      fs.mkdirSync(carpetaImagenes);
    }

    const rutaCompleta = path.join(carpetaImagenes, rutaArchivo);

    try {
      fs.writeFileSync(rutaCompleta, imagenBuffer);
      console.log(`Imagen guardada en ${rutaCompleta}`);
      return rutaCompleta;
    } catch (error) {
      console.error('Error al guardar la imagen:', error.message);
      throw error;
    }
  }
}

export default AdministradordeImagenes;
