import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelListUser from '../components/HotelListUser';
import Filter from '../components/Filter';

function Servicios() {
  const [hotels, setHotels] = useState([]);
  const [filteredHotels, setFilteredHotels] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchHotelImage = async (idAlojamiento) => {
    try {
      const response = await fetch(`http://localhost:3001/imagen/getImagen/${idAlojamiento}`);
      const data = await response.json();
      return data.RutaArchivo;
    } catch (error) {
      console.error('Error fetching hotel image:', error);
      return null;
    }
  };

  const fetchHotels = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/getAlojamientos');
      const data = await response.json();
      const formattedHotels = await Promise.all(
        data.map(async (hotel) => {
          const image = await fetchHotelImage(hotel.idAlojamiento);
          return {
            id: hotel.idAlojamiento,
            name: hotel.Titulo,
            latitud: hotel.Latitud,
            longitud: hotel.Longitud,
            price: hotel.PrecioPorDia,
            images: hotel.imagenes || [],
            description: hotel.Descripción,
            bedrooms: hotel.CantidadDormitorios,
            bathrooms: hotel.CantidadBanios,
            estado: hotel.Estado,
            tipoId: hotel.idTipoAlojamiento, // Agrega esta línea
            image: image || 'default_image_url.jpg',
          };
        })
      );
      setHotels(formattedHotels);
      setFilteredHotels(formattedHotels);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching hotels:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHotels();
  }, []);

  const handleFilterChange = (filters) => {
    let filtered = hotels;

    if (filters.type) {
      filtered = filtered.filter((hotel) => hotel.tipoId === parseInt(filters.type));
    }

    if (filters.availability) {
      filtered = filtered.filter((hotel) => hotel.estado.toLowerCase().includes(filters.availability.toLowerCase()));
    }

    if (filters.priceRange.min) {
      filtered = filtered.filter((hotel) => hotel.price >= parseFloat(filters.priceRange.min));
    }

    if (filters.priceRange.max) {
      filtered = filtered.filter((hotel) => hotel.price <= parseFloat(filters.priceRange.max));
    }

    if (filters.bedrooms) {
      filtered = filtered.filter((hotel) => hotel.bedrooms >= parseInt(filters.bedrooms));
    }

    if (filters.bathrooms) {
      filtered = filtered.filter((hotel) => hotel.bathrooms >= parseInt(filters.bathrooms));
    }

    setFilteredHotels(filtered);
  };

  const handleClearFilters = () => {
    setFilteredHotels(hotels);
  };

  return (
    <>
      <Navbar />
      <Hero
        cName="hero-mid"
        heroImg="https://i.postimg.cc/qqBp3KdY/heather-gill-Wlhfy-AHmp-II-unsplash.jpg"
        title="Alojamientos"
        btnClass="hide"
      />
      <div className="catalog">
        <Filter onFilterChange={handleFilterChange} onClearFilters={handleClearFilters} />
        {loading ? (
          <p>Cargando Hoteles...</p>
        ) : (
          <HotelListUser hotels={filteredHotels} />
        )}
      </div>
      <Footer />
    </>
  );
}

export default Servicios;
