import React, { useState, useEffect } from 'react';
import obtenerCiudadCoordenadas from './obtenerCiudadCoordenadas'; // Ajusta la ruta según donde hayas definido la función
import './HotelCard.css';

const HotelCard = ({ hotel }) => {
  const [city, setCity] = useState('');

  useEffect(() => {
    const fetchCity = async () => {
      const cityName = await obtenerCiudadCoordenadas(hotel.latitud, hotel.longitud);
      setCity(cityName);
    };

    fetchCity();
  }, [hotel.latitud, hotel.longitud]);

  return (
    <div className="hotel-card">
      <h2>{hotel.name}</h2>
      <p>City: {city}</p>
      <p>Price: ${hotel.price}</p>
      <div className="hotel-images">
        {hotel.images.map((image) => (
          <img key={image.id} src={image.url} alt={`Hotel ${hotel.name}`} />
        ))}
      </div>
    </div>
  );
};

export default HotelCard;
