import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelList from '../components/HotelList';
import Filter from '../components/Filter';

const hotelsData = [
    {
      id: 1,
      name: 'Hotel A',
      location: 'New York',
      rating: 4.5,
      price: 120,
      images: [
        { id: 'img1', url: 'https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D' },
        { id: 'img2', url: 'https://example.com/image2.jpg' },
      ],
    },
    {
      id: 2,
      name: 'Hotel B',
      location: 'Los Angeles',
      rating: 4.0,
      price: 150,
      images: [
        { id: 'img3', url: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D' },
        { id: 'img4', url: 'https://example.com/image4.jpg' },
      ],
    },
    {
      id: 3,
      name: 'Hotel C',
      location: 'Chicago',
      rating: 3.5,
      price: 90,
      images: [
        { id: 'img5', url: 'https://images.unsplash.com/photo-1551882547-ff40c63fe5fa?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D' },
        { id: 'img6', url: 'https://example.com/image6.jpg' },
      ],
    },
  ];

function Servicios() {
  const [hotels, setHotels] = useState(hotelsData);

  const handleFilterChange = (filters) => {
    const filteredHotels = hotelsData.filter((hotel) => {
      return (
        (!filters.location || hotel.location.toLowerCase().includes(filters.location.toLowerCase())) &&
        (filters.minRating === 0 || hotel.rating >= filters.minRating) &&
        (filters.maxPrice === 0 || hotel.price <= filters.maxPrice)
      );
    });
    setHotels(filteredHotels);
  };

  return (
    <>
      <Navbar />
      <Hero
        cName="hero-mid"
        heroImg="https://i.postimg.cc/qqBp3KdY/heather-gill-Wlhfy-AHmp-II-unsplash.jpg"
        title="Servicios"
        btnClass="hide"
      />
      <div className="catalog">
        <Filter onFilterChange={handleFilterChange} />
        <HotelList hotels={hotels} />
      </div>
      <Footer />
    </>
  );
}

export default Servicios;
