import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelList from '../components/HotelList';
import Filter from '../components/Filter';

function Servicios() {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchHotels = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/getAlojamientos');
      const data = await response.json();
      // Ajusta las propiedades según los datos devueltos por la API
      const formattedHotels = data.map(hotel => ({
        id: hotel.idAlojamiento,
        name: hotel.Titulo,
        location: hotel.Ubicacion,
        price: hotel.PrecioPorDia,
        images: hotel.imagenes || [] // Ajusta si hay datos de imágenes
      }));
      setHotels(formattedHotels);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching hotels:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHotels();
  }, []);

  const handleFilterChange = (filters) => {
    const filteredHotels = hotels.filter((hotel) => {
      return (
        (!filters.location || hotel.location.toLowerCase().includes(filters.location.toLowerCase())) &&
        (filters.minPrice === 0 || hotel.price <= filters.minPrice)
      );
    });
    setHotels(filteredHotels);
  };

  const handleClearFilters = () => {
    fetchHotels(); // Vuelve a cargar los hoteles desde la API
  };

  const handleDeleteHotel = async (id) => {
    try {
      await fetch(`http://localhost:3001/alojamiento/deleteAlojamiento/${id}`, {
        method: 'DELETE',
      });
      setHotels(hotels.filter(hotel => hotel.id !== id));
    } catch (error) {
      console.error('Error deleting hotel:', error);
    }
  };

  const handleUpdateHotel = async (id, updatedHotel) => {
    try {
      await fetch(`http://localhost:3001/alojamiento/putAlojamiento/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedHotel),
      });
      setHotels(hotels.map(hotel => (hotel.id === id ? updatedHotel : hotel)));
    } catch (error) {
      console.error('Error updating hotel:', error);
    }
  };

  return (
    <>
      <Navbar />
      <Hero
        cName="hero-mid"
        heroImg="https://i.postimg.cc/qqBp3KdY/heather-gill-Wlhfy-AHmp-II-unsplash.jpg"
        title="Servicios"
        btnClass="hide"
      />
      <div className="catalog">
        <Filter onFilterChange={handleFilterChange} onClearFilters={handleClearFilters} />
        {loading ? (
          <p>Loading hotels...</p>
        ) : (
          <HotelList hotels={hotels} onDeleteHotel={handleDeleteHotel} onUpdateHotel={handleUpdateHotel} />
        )}
      </div>
      <Footer />
    </>
  );
}

export default Servicios;
