import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegistrarAlojamiento.css'; // Importa el archivo CSS para los estilos del formulario
import Sidebar from './Sidebar'; // Importamos el componente Sidebar
import Modal from './Modal'; // Importamos el componente Modal

const RegistrarAlojamiento = () => {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({
    name: '',
    latitud: '',
    longitud: '',
    price: '',
    description: '',
    bedrooms: '',
    bathrooms: '',
    estado: 'Disponible',
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleCreate = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/createAlojamiento', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          Titulo: formValues.name,
          Descripcion: formValues.description,
          TipoAlojamiento: 1, // Esto depende de tu API
          Latitud: parseFloat(formValues.latitud),
          Longitud: parseFloat(formValues.longitud),
          PrecioPorDia: parseFloat(formValues.price),
          CantidadDormitorios: parseInt(formValues.bedrooms),
          CantidadBanios: parseInt(formValues.bathrooms),
          Estado: formValues.estado,
        }),
      });

      const data = await response.json();
      console.log('Response status:', response.status);
      console.log('Response data:', data);

      if (response.ok) {
        console.log('Alojamiento creado correctamente');
        clearForm();
        setModalMessage('Alojamiento creado correctamente');
      } else {
        console.error('Error al crear el alojamiento:', data.message || response.statusText);
        setModalMessage(`Error al crear el alojamiento: ${data.message || response.statusText}`);
      }
      setIsModalOpen(true);
    } catch (error) {
      console.error('Error al crear el alojamiento:', error);
      setModalMessage('Error al crear el alojamiento');
      setIsModalOpen(true);
    }
  };

  const clearForm = () => {
    setFormValues({
      name: '',
      latitud: '',
      longitud: '',
      price: '',
      description: '',
      bedrooms: '',
      bathrooms: '',
      estado: 'Disponible',
    });
  };

  const handleCheckInClick = () => {
    navigate('/');
  };

  const handleRegistrarAlojamientoClick = () => {
    navigate('/registrar-alojamiento');
  };

  const handleAdministrarImagenesClick = () => {
    navigate('/administrador-imagenes');
  };

  const handleGestionAlojamientosClick = () => {
    navigate('/gestion-de-alojamientos');
  };

  const handleFileUpload = async () => {
    try {
      if (!selectedFile) {
        console.error('No se ha seleccionado ningún archivo.');
        return;
      }

      const formData = new FormData();
      formData.append('file', selectedFile);

      const response = await fetch('http://localhost:3001/imagen/createImagen', {
        method: 'POST',
        body: formData,
      });

      const imageData = await response.json();
      console.log('Image upload response:', imageData);

      if (response.ok) {
        console.log('Imagen subida correctamente');
        // Aquí podrías actualizar el estado o hacer algo adicional después de subir la imagen
      } else {
        console.error('Error al subir la imagen:', imageData.message || response.statusText);
        // Manejar el error según sea necesario
      }
    } catch (error) {
      console.error('Error al subir la imagen:', error);
    }
  };

  return (
    <div className="dashboard">
      <Sidebar
        role="admin"
        handleRegistrarAlojamientoClick={handleRegistrarAlojamientoClick}
        handleAdministrarImagenesClick={handleAdministrarImagenesClick}
        handleCheckInClick={handleCheckInClick}
        handleGestionAlojamientosClick={handleGestionAlojamientosClick}
      />
      <main className="main-content">
        <div className="registrar-alojamiento">
          <h2>Registrar Alojamiento</h2>
          <form onSubmit={(e) => e.preventDefault()} className="form-container">
            <div className="form-group">
              <label>Nombre:</label>
              <input type="text" name="name" value={formValues.name} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Latitud:</label>
              <input type="text" name="latitud" value={formValues.latitud} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Longitud:</label>
              <input type="text" name="longitud" value={formValues.longitud} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Precio:</label>
              <input type="text" name="price" value={formValues.price} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Descripción:</label>
              <textarea name="description" value={formValues.description} onChange={handleChange} className="form-input textarea" />
            </div>
            <div className="form-group">
              <label>Dormitorios:</label>
              <input type="text" name="bedrooms" value={formValues.bedrooms} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Baños:</label>
              <input type="text" name="bathrooms" value={formValues.bathrooms} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Estado:</label>
              <input type="text" name="estado" value={formValues.estado} onChange={handleChange} className="form-input" />
            </div>
            <div className="form-group">
              <label>Subir Imagen:</label>
              <input type="file" accept="image/*" onChange={handleFileChange} className="form-input" />
            </div>
            <div className="button-group">
              <button type="button" onClick={handleFileUpload} className="form-button">Subir Imagen</button>
              <button type="submit" onClick={handleCreate} className="form-button">Registrar</button>
            </div>
          </form>
        </div>
      </main>
      {isModalOpen && <Modal message={modalMessage} onClose={() => setIsModalOpen(false)} />}
    </div>
  );
};

export default RegistrarAlojamiento;
