import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelList from '../components/HotelList';
import Filter from '../components/Filter';

function Servicios() {
  const [hotels, setHotels] = useState([]);
  const [isLoading, setIsLoading] = useState(false); // Track loading state
  const [error, setError] = useState(null); // Track errors

  useEffect(() => {
    const fetchHotels = async () => {
      setIsLoading(true); // Set loading state to true
      setError(null); // Clear any previous errors

      try {
        const response = await fetch('http://localhost:3001/alojamiento/getAlojamientos'); // Fetch hotels from API
        if (!response.ok) {
          throw new Error(`Error fetching hotels: ${response.statusText}`); // Handle non-200 responses
        }
        const data = await response.json();
        setHotels(data);
      } catch (err) {
        setError(err.message); // Set error message
      } finally {
        setIsLoading(false); // Set loading state to false after fetching or error
      }
    };

    fetchHotels(); // Call the fetchHotels function
  }, []);
  
