import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Hero from '../components/Hero';
import Footer from '../components/Footer';
import HotelList from '../components/HotelList';
import Filter from '../components/Filter';
import './Servicios.css'; // Importar el CSS
import obtenerCiudadCoordenadas from '../components/obtenerCiudadCoordenadas'; // Importa la función de obtenerCiudadCoordenadas

function Servicios() {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedHotel, setSelectedHotel] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchHotels = async () => {
    try {
      const response = await fetch('http://localhost:3001/alojamiento/getAlojamientos');
      const data = await response.json();
      const formattedHotels = data.map(hotel => ({
        id: hotel.idAlojamiento,
        name: hotel.Titulo,
        latitud: hotel.Latitud,
        longitud: hotel.Longitud,
        price: hotel.PrecioPorDia,
        images: hotel.imagenes || [],
        description: hotel.Descripción,
        bedrooms: hotel.CantidadDormitorios,
        bathrooms: hotel.CantidadBanios,
        estado: hotel.Estado,
      }));
      setHotels(formattedHotels);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching hotels:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHotels();
  }, []);

  const handleFilterChange = (filters) => {
    const filteredHotels = hotels.filter((hotel) => {
      return (
        (!filters.location || hotel.location.toLowerCase().includes(filters.location.toLowerCase())) &&
        (filters.minPrice === 0 || hotel.price <= filters.minPrice)
      );
    });
    setHotels(filteredHotels);
  };

  const handleClearFilters = () => {
    fetchHotels(); // Vuelve a cargar los hoteles desde la API
  };

  const handleViewMore = (hotel) => {
    setSelectedHotel(hotel);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedHotel(null);
  };

  return (
    <>
      <Navbar />
      <Hero
        cName="hero-mid"
        heroImg="https://i.postimg.cc/qqBp3KdY/heather-gill-Wlhfy-AHmp-II-unsplash.jpg"
        title="Servicios"
        btnClass="hide"
      />
      <div className="catalog">
        <Filter onFilterChange={handleFilterChange} onClearFilters={handleClearFilters} />
        {loading ? (
          <p>Cargando Hoteles...</p>
        ) : (
          <HotelList hotels={hotels} onViewMore={handleViewMore} />
        )}
      </div>
      {isModalOpen && selectedHotel && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>
              &times;
            </span>
            <h2>{selectedHotel.name}</h2>
            <p>Descripción: {selectedHotel.description}</p>
            <p>Precio por día: ${selectedHotel.price}</p>
            <p>Dormitorios: {selectedHotel.bedrooms}</p>
            <p>Baños: {selectedHotel.bathrooms}</p>
            <p>Estado: {selectedHotel.estado}</p>
          </div>
        </div>
      )}
      <Footer />
    </>
  );
}

export default Servicios;
